import React, { useState } from 'react';
import Header from '../components/Header';
import NewStackModal from '../components/NewStackModel'
import { Link } from 'react-router-dom';
import 'openagi_app\src\App.css';

const MainPage = () => {
  const [showModal, setShowModal] = useState(false);

  const handleNewStackClick = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  return (
    <div>
      <Header />
      <main style={styles.main}>
        <h1>Welcome to OpenAGI</h1>
        <button onClick={handleNewStackClick} style={styles.newStackButton}>+ New Stack</button>
        <Link to="/stacks" style={styles.link}>View Existing Stacks</Link>
      </main>
      {showModal && <NewStackModal onClose={handleCloseModal} />}
    </div>
  );
};

const styles = {
  main: {
    padding: '20px',
    textAlign: 'center'
  },
  newStackButton: {
    padding: '10px 20px',
    fontSize: '1.2em',
    cursor: 'pointer'
  },
  link: {
    display: 'block',
    marginTop: '20px',
    fontSize: '1.1em'
  }
};

export default MainPage;
