import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import Header from '../components/Header';
import Sidebar from '../components/Sidebar';
import Footer from '../components/Footer';

const StacksDefaultPage = () => {
    const { id } = useParams();
    const [stack, setStack] = useState(null);

    useEffect(() => {
        fetch(`http://127.0.0.1:8000/stacks/${id}`)
            .then(response => response.json())
            .then(data => setStack(data))
            .catch(error => console.error('Error fetching stack details:', error));
    }, [id]);

    if (!stack) {
        return <p>Loading...</p>;
    }

    return (
        <>
            <Header />
            <Sidebar />
            <main>
                <h2>{stack.name}</h2>
                <p>{stack.description}</p>
                <Link to={`/stacks/${id}/build`}>Build Stack</Link>
            </main>
            <Footer />
        </>
    );
};

export default StacksDefaultPage;
